+*{
+    margin: 0;
+    padding: 0;
+    font-family: 'Roboto', sans-serif;
+}
+body{
+    overflow-x: hidden;
+    background-color: #e3f0f3;
+}
+html{
+    scroll-behavior: smooth;
+}
+
+/* nav Section */
+nav{
+    display: flex;
+    justify-content: space-between;
+    align-items: center;
+    padding: 10px;
+    background-color: #26575b;
+    top: 0;
+    left: 0;
+    right: 0;
+    position: fixed;
+    z-index: 10000;
+}
+.logo{
+    padding-left: 20px;
+    display: flex;
+    align-items: center;
+}
+.logo p{
+    color: white;
+    font-size: 20px;
+    font-weight: 500;
+    margin-left: 10px;
+}
+ul{
+    align-items: center;
+    list-style: none;
+    display: flex;
+    gap: 20px;
+    
+}
+li a{
+    text-decoration: none;
+    color: white;
+    font-weight: 500;
+    font-size: 18px;
+    margin: auto 30px ;
+    align-items: center;
+    transition: 0.3s ease;
+}
+li a:hover{
+    color: #f0f0f0;
+    border-bottom: 2px solid #f0f0f0;
+    padding-bottom: 5px;
+}
+nav .right{
+    display: flex;
+    align-items: center;
+    gap: 15px;
+}
+nav .right .login-btn,  .language-btn{
+    padding: 8px 15px;
+    border: none;
+    border-radius: 5px;
+    background-color: #f0f0f0;
+    cursor: pointer;
+    font-weight: 500;
+    font-size: 16px;
+}
+nav .right .cart-btn{
+    color: #f0f0f0;
+    font-size: 20px;
+    padding-right: 20px;
+
+}
+
+/* Hero Section */
+/* Hero Section */
+/* Hero Section */
+.hero {
+    position: relative;
+    width: 100%;
+    height: 100vh; /* يغطي الشاشة كلها */
+    display: flex;
+    align-items: center;
+    justify-content: flex-start; /* Changed from center to flex-start */
+    overflow: hidden;
+}
+
+.hero-img {
+    position: absolute;
+    width: 100%;
+    height: 100%;
+    object-fit: cover; /* يخلي الصورة مغطية الخلفية بدون تمدد غريب */
+    top: 0;
+    left: 0;
+    z-index: 0;
+}
+
+.hero-content {
+    position: relative;
+    z-index: 1;
+    text-align: left;
+    max-width: 700px;
+    color: white;
+    padding: 20px;
+    transform: translateY(20px); /* Moves the content slightly lower */
+    display: flex;
+    flex-direction: column;
+    align-items: flex-start; /* Aligns all content to the left */
+    margin-left: 50px; /* Adds some space from the left edge */
+    margin-top: 300px;
+    
+}
+
+.hero-content h1 {
+    font-size: 5rem;
+    font-weight: bold;
+    margin-bottom: 1rem;
+    width: 100%; /* Ensures full width for proper alignment */
+    
+}
+
+.hero-content p {
+    font-size: 1.5rem;
+    margin-bottom: 1.5rem;
+    width: 100%; /* Ensures full width for proper alignment */
+    padding-bottom: 20px;
+}
+
+.hero-content .button-container {
+    width: 100%;
+    display: flex;
+    justify-content: center; /* Centers the button horizontally */
+    margin-top: auto; /* Pushes the button to the bottom */
+    margin-left: 340px;
+}
+
+.hero-content button {
+    background-color: rgba(255, 255, 255, 0.2);
