+    padding: 0.9rem 1.5rem;
+    font-size: 1rem;
+    color: white;
+    border-radius: 8px;
+    cursor: pointer;
+    
+    align-self: center; /* Centers the button within the container */
+    transition: 0.8s ease;
+}
+
+.hero-content button:hover {
+    background-color: white;
+    color: black;
+}
