.stitch-design {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: relative;
  background-color: #ffffff;
}

.stitch-design .depth-frame {
  display: flex;
  flex-direction: column;
  min-height: 800px;
  align-items: flex-start;
  position: relative;
  align-self: stretch;
  width: 100%;
  flex: 0 0 auto;
  background-color: #f6e8df;
}

.stitch-design .div {
  display: flex;
  flex-direction: column;
  height: 1506px;
  align-items: flex-start;
  position: relative;
  align-self: stretch;
  width: 100%;
}

.stitch-design .depth-frame-2 {
  align-items: center;
  justify-content: space-between;
  padding: 12px 40px;
  flex: 0 0 auto;
  background-color: #26575a;
  border-bottom-width: 1px;
  border-bottom-style: solid;
  border-color: #e5e8ea;
  display: flex;
  position: relative;
  align-self: stretch;
  width: 100%;
}

.stitch-design .depth-frame-3 {
