  /* Reset and Base Styles */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Roboto', sans-serif;
            line-height: 1.6;
            color: #333;
            overflow-x: hidden;
            background-color: #e3f0f3;
            padding-top: 70px; /* Account for fixed navbar */
        }

        html {
            scroll-behavior: smooth;
            scroll-padding-top: 70px; /* Offset for fixed navbar */
        }

        /* Navigation Styles (from homepage) */
        nav {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            z-index: 10000;
            background-color: #26575b;
            box-shadow: 0 2px 20px rgba(0, 0, 0, 0.1);
            transition: all 0.3s ease;
        }

        .nav-container {
            max-width: 1400px;
            margin: 0 auto;
            padding: 0 20px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            height: 70px;
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 12px;
            z-index: 10001;
        }

        .logo-img {
            width: 40px;
            height: 40px;
            border-radius: 8px;
        }

        .logo p {
            color: white;
            font-size: 1.4rem;
            font-weight: 700;
            margin: 0;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        .nav-menu {
            display: flex;
            align-items: center;
            gap: 40px;
            transition: all 0.3s ease;
        }

        .nav-links {
            display: flex;
            align-items: center;
            list-style: none;
            margin: 0;
            padding: 0;
            gap: 30px;
        }

        .nav-link {
            text-decoration: none;
            color: white;
            font-weight: 500;
            font-size: 1rem;
            padding: 8px 0;
            position: relative;
            transition: all 0.3s ease;
        }

        .nav-link::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 0;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, #ffd700, #ff8c00);
            transition: width 0.3s ease;
        }

        .nav-link:hover::after,
        .nav-link.active::after {
            width: 100%;
        }

        .nav-link:hover,
        .nav-link.active {
            color: #ffd700;
        }

        .nav-right {
            display: flex;
            align-items: center;
            gap: 15px;
        }

        .login-btn {
            display: flex;
            align-items: center;
            gap: 8px;
            padding: 8px 20px;
            border: none;
            border-radius: 25px;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            font-weight: 500;
            font-size: 0.9rem;
            cursor: pointer;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .login-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: translateY(-1px);
        }

        .language-btn {
            width: 40px;
            height: 40px;
            border: none;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            cursor: pointer;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            justify-content: center;
            backdrop-filter: blur(10px);
        }

        .language-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: scale(1.1);
        }

        .cart-btn {
            position: relative;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            display: flex;
            align-items: center;
            justify-content: center;
            text-decoration: none;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .cart-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: scale(1.1);
        }

        .cart-count {
            position: absolute;
            top: -5px;
            right: -5px;
            background: linear-gradient(135deg, #ff6b6b, #ff8e53);
            color: white;
            font-size: 0.7rem;
            font-weight: 600;
            width: 18px;
            height: 18px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        /* Checkout Page Styles */
        .checkout-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 40px 20px;
            display: grid;
            grid-template-columns: 1fr 400px;
            gap: 40px;
        }

        .checkout-header {
            margin-bottom: 30px;
        }

        .checkout-title {
            font-size: 2.5rem;
            color: #26575b;
            font-weight: 700;
            margin-bottom: 10px;
        }

        .checkout-steps {
            display: flex;
            align-items: center;
            gap: 20px;
            margin-bottom: 30px;
        }

        .step {
            display: flex;
            align-items: center;
            gap: 10px;
            font-weight: 500;
        }

        .step-number {
            width: 30px;
            height: 30px;
            border-radius: 50%;
            background: #e0e0e0;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 0.9rem;
            font-weight: 600;
        }

        .step.active .step-number {
            background: #18685b;
            color: white;
        }

        .step.completed .step-number {
            background: #4CAF50;
            color: white;
        }

        .step-divider {
            height: 2px;
            width: 40px;
            background: #e0e0e0;
        }

        .checkout-section {
            background: white;
            border-radius: 12px;
            padding: 30px;
            margin-bottom: 30px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
        }

        .section-title {
            font-size: 1.5rem;
            color: #26575b;
            margin-bottom: 20px;
            font-weight: 600;
            display: flex;
            align-items: center;
            gap: 10px;
        }

        .section-title i {
            color: #18685b;
        }

        .form-group {
            margin-bottom: 20px;
        }

        .form-row {
            display: grid;
            grid-template-columns: 1fr 1fr;
            gap: 15px;
        }

        .form-label {
            display: block;
            margin-bottom: 8px;
            font-weight: 500;
            color: #333;
        }

        .form-input {
            width: 100%;
            padding: 12px 15px;
            border: 1px solid #ddd;
            border-radius: 8px;
            font-size: 1rem;
            transition: border-color 0.3s ease;
        }

        .form-input:focus {
            outline: none;
            border-color: #18685b;
        }

        .payment-methods {
            display: flex;
            flex-direction: column;
            gap: 15px;
        }

        .payment-method {
            border: 1px solid #ddd;
            border-radius: 8px;
            padding: 20px;
            cursor: pointer;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            gap: 15px;
        }

        .payment-method:hover {
            border-color: #18685b;
            background: #f9f9f9;
        }

        .payment-method.selected {
            border-color: #18685b;
            background: rgba(24, 104, 91, 0.05);
        }

        .payment-icon {
            width: 50px;
            height: 50px;
            border-radius: 8px;
            display: flex;
            align-items: center;
            justify-content: center;
            font-size: 1.5rem;
            color: white;
        }

        .bankak-icon {
            background: linear-gradient(135deg, #1a3c40, #26575b);
        }

        .ocash-icon {
            background: linear-gradient(135deg, #ff8c00, #ff6b6b);
        }

        .cod-icon {
            background: linear-gradient(135deg, #4CAF50, #45a049);
        }

        .payment-details {
            flex-grow: 1;
        }

        .payment-name {
            font-weight: 600;
            margin-bottom: 5px;
            color: #26575b;
        }

        .payment-description {
            font-size: 0.9rem;
            color: #666;
        }

        .payment-instructions {
            margin-top: 20px;
            padding: 15px;
            background: #f5f5f5;
            border-radius: 8px;
            font-size: 0.9rem;
            display: none;
        }

        .payment-instructions.active {
            display: block;
        }

        .order-summary {
            background: white;
            border-radius: 12px;
            padding: 30px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
            position: sticky;
            top: 90px;
        }

        .summary-title {
            font-size: 1.5rem;
            color: #26575b;
            margin-bottom: 20px;
            font-weight: 600;
        }

        .order-items {
            margin-bottom: 20px;
            max-height: 300px;
            overflow-y: auto;
        }

        .order-item {
            display: flex;
            gap: 15px;
            padding: 15px 0;
            border-bottom: 1px solid #eee;
        }

        .order-item:last-child {
            border-bottom: none;
        }

        .item-image {
            width: 70px;
            height: 70px;
            border-radius: 8px;
            overflow: hidden;
            flex-shrink: 0;
        }

        .item-image img {
            width: 100%;
            height: 100%;
            object-fit: cover;
        }

        .item-details {
            flex-grow: 1;
        }

        .item-name {
            font-weight: 600;
            margin-bottom: 5px;
            color: #333;
        }

        .item-price {
            color: #18685b;
            font-weight: 600;
        }

        .item-quantity {
            color: #666;
            font-size: 0.9rem;
        }

        .summary-totals {
            border-top: 1px solid #eee;
            padding-top: 20px;
        }

        .total-row {
            display: flex;
            justify-content: space-between;
            margin-bottom: 10px;
        }

        .total-label {
            color: #666;
        }

        .total-amount {
            font-weight: 600;
        }

        .grand-total {
            font-size: 1.2rem;
            font-weight: 700;
            color: #26575b;
            border-top: 1px solid #eee;
            padding-top: 15px;
            margin-top: 15px;
        }

        .checkout-btn {
            width: 100%;
            background: linear-gradient(135deg, #18685b, #26575b);
            color: white;
            border: none;
            padding: 15px;
            border-radius: 8px;
            font-size: 1.1rem;
            font-weight: 600;
            cursor: pointer;
            transition: all 0.3s ease;
            margin-top: 20px;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
        }

        .checkout-btn:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(24, 104, 91, 0.3);
        }

        .checkout-btn:disabled {
            background: #ccc;
            cursor: not-allowed;
            transform: none;
            box-shadow: none;
        }

        .security-notice {
            display: flex;
            align-items: center;
            gap: 10px;
            margin-top: 15px;
            font-size: 0.9rem;
            color: #666;
        }

        .security-notice i {
            color: #4CAF50;
        }

        /* Footer Styles */
        footer {
            background: linear-gradient(180deg, #f6e9df 0%, #fff 100%);
            padding: 48px 20px;
            border-top: 1px solid rgba(15, 122, 99, 0.07);
            margin-top: 60px;
        }

        .footer-inner {
            max-width: 1200px;
            margin: 0 auto;
            display: grid;
            gap: 28px;
            grid-template-columns: 1fr;
        }

        .footer-brand {
            display: flex;
            gap: 16px;
            align-items: flex-start;
        }

        .brand-logo {
            width: 64px;
            height: 64px;
            flex: 0 0 64px;
            border-radius: 10px;
            background: linear-gradient(135deg, #0f7a63, #0c5f4d);
            display: grid;
            place-items: center;
            color: #fff;
            font-weight: 700;
            font-size: 20px;
            box-shadow: 0 6px 18px rgba(12, 95, 77, 0.10);
        }

        .brand-info h3 {
            margin: 0 0 6px 0;
            font-size: 18px;
            line-height: 1;
        }

        .brand-info p {
            margin: 0;
            color: #5b6d70;
            font-size: 14px;
        }

        .footer-columns {
            display: grid;
            grid-template-columns: repeat(2, minmax(0, 1fr));
            gap: 18px;
        }

        .col h4 {
            margin: 0 0 12px 0;
            font-size: 15px;
            color: #20323a;
        }

        .col ul {
            list-style: none;
            padding: 0;
            margin: 0;
            display: grid;
            gap: 8px;
        }

        .col a {
            color: #5b6d70;
            text-decoration: none;
            font-size: 14px;
            transition: color .18s ease;
        }

        .col a:hover {
            color: #0c5f4d;
        }

        .footer-bottom {
            margin-top: 18px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            gap: 12px;
            border-top: 1px solid rgba(15, 122, 99, 0.06);
            padding-top: 18px;
            flex-wrap: wrap;
            max-width: 1200px;
            margin: 18px auto 0;
        }

        .copyright {
            color: #5b6d70;
            font-size: 13px;
        }

        .policies {
            display: flex;
            gap: 12px;
            align-items: center;
        }

        .policies a {
            color: #5b6d70;
            font-size: 13px;
            text-decoration: none;
        }

        /* Responsive Styles */
        @media (max-width: 1024px) {
            .checkout-container {
                grid-template-columns: 1fr;
            }
            
            .order-summary {
                position: static;
            }
        }

        @media (max-width: 768px) {
            .form-row {
                grid-template-columns: 1fr;
            }
            
            .checkout-title {
                font-size: 2rem;
            }
            
            .checkout-steps {
                flex-wrap: wrap;
                gap: 10px;
            }
            
            .step-divider {
                display: none;
            }
            
            .footer-columns {
                grid-template-columns: 1fr;
            }
        }

        @media (max-width: 576px) {
            .checkout-section {
                padding: 20px;
            }
            
            .payment-method {
                flex-direction: column;
                text-align: center;
            }
            
            .footer-bottom {
                flex-direction: column;
                text-align: center;
            }
        }
