 // Wishlist functionality
        document.querySelectorAll('.product-wishlist').forEach(button => {
            button.addEventListener('click', function() {
                const icon = this.querySelector('i');
                if (icon.classList.contains('far')) {
                    icon.classList.remove('far');
                    icon.classList.add('fas', 'active');
                } else {
                    icon.classList.remove('fas', 'active');
                    icon.classList.add('far');
                }
            });
        });

        // Add to cart functionality
        document.querySelectorAll('.btn-add-cart').forEach(button => {
            button.addEventListener('click', function() {
                const currentCount = parseInt(document.querySelector('.cart-count').textContent);
                document.querySelector('.cart-count').textContent = currentCount + 1;
                
                // Visual feedback
                this.innerHTML = '<i class="fas fa-check"></i> Added';
                this.style.background = 'linear-gradient(135deg, #4CAF50, #45a049)';
                
                setTimeout(() => {
                    this.innerHTML = '<i class="fas fa-shopping-cart"></i> Add to Cart';
                    this.style.background = 'linear-gradient(135deg, #18685b, #26575b)';
                }, 1500);
            });
        });

        // Filter functionality
        document.querySelectorAll('.filter-option input').forEach(checkbox => {
            checkbox.addEventListener('change', function() {
                // In a real implementation, this would filter the products
                console.log(`Filter changed: ${this.id} - ${this.checked}`);

            });
        });

        // Sort functionality
        document.querySelector('.sort-select').addEventListener('change', function() {
            // In a real implementation, this would sort the products
            console.log(`Sort by: ${this.value}`);
        });
