 /* Reset and Base Styles */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }

        body {
            font-family: 'Roboto', sans-serif;
            line-height: 1.6;
            color: #333;
            overflow-x: hidden;
            background-color: #e3f0f3;
            padding-top: 70px; /* Account for fixed navbar */
        }

        html {
            scroll-behavior: smooth;
            scroll-padding-top: 70px; /* Offset for fixed navbar */
        }

        /* Navigation Styles (from homepage) */
        nav {
            position: fixed;
            top: 0;
            left: 0;
            right: 0;
            z-index: 10000;
            background-color: #26575b;
            box-shadow: 0 2px 20px rgba(0, 0, 0, 0.1);
            transition: all 0.3s ease;
        }

        .nav-container {
            max-width: 1400px;
            margin: 0 auto;
            padding: 0 20px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            height: 70px;
        }

        .logo {
            display: flex;
            align-items: center;
            gap: 12px;
            z-index: 10001;
        }

        .logo-img {
            width: 40px;
            height: 40px;
            border-radius: 8px;
        }

        .logo p {
            color: white;
            font-size: 1.4rem;
            font-weight: 700;
            margin: 0;
            text-transform: uppercase;
            letter-spacing: 0.5px;
        }

        .nav-menu {
            display: flex;
            align-items: center;
            gap: 40px;
            transition: all 0.3s ease;
        }

        .nav-links {
            display: flex;
            align-items: center;
            list-style: none;
            margin: 0;
            padding: 0;
            gap: 30px;
        }

        .nav-link {
            text-decoration: none;
            color: white;
            font-weight: 500;
            font-size: 1rem;
            padding: 8px 0;
            position: relative;
            transition: all 0.3s ease;
        }

        .nav-link::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 0;
            width: 0;
            height: 2px;
            background: linear-gradient(90deg, #ffd700, #ff8c00);
            transition: width 0.3s ease;
        }

        .nav-link:hover::after,
        .nav-link.active::after {
            width: 100%;
        }

        .nav-link:hover,
        .nav-link.active {
            color: #ffd700;
        }

        .nav-right {
            display: flex;
            align-items: center;
            gap: 15px;
        }

        .login-btn {
            display: flex;
            align-items: center;
            gap: 8px;
            padding: 8px 20px;
            border: none;
            border-radius: 25px;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            font-weight: 500;
            font-size: 0.9rem;
            cursor: pointer;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .login-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: translateY(-1px);
        }

        .language-btn {
            width: 40px;
            height: 40px;
            border: none;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            cursor: pointer;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            justify-content: center;
            backdrop-filter: blur(10px);
        }

        .language-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: scale(1.1);
        }

        .cart-btn {
            position: relative;
            width: 40px;
            height: 40px;
            border-radius: 50%;
            background: rgba(255, 255, 255, 0.15);
            color: white;
            display: flex;
            align-items: center;
            justify-content: center;
            text-decoration: none;
            transition: all 0.3s ease;
            backdrop-filter: blur(10px);
        }

        .cart-btn:hover {
            background: rgba(255, 255, 255, 0.25);
            transform: scale(1.1);
        }

        .cart-count {
            position: absolute;
            top: -5px;
            right: -5px;
            background: linear-gradient(135deg, #ff6b6b, #ff8e53);
            color: white;
            font-size: 0.7rem;
            font-weight: 600;
            width: 18px;
            height: 18px;
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        /* Product Page Styles */
        .products-hero {
            background: linear-gradient(135deg, #1a3c40 0%, #26575b 50%, #2d6b6f 100%);
            color: white;
            padding: 60px 20px;
            text-align: center;
        }

        .products-hero h1 {
            font-size: 3rem;
            margin-bottom: 20px;
            font-weight: 800;
        }

        .products-hero p {
            font-size: 1.2rem;
            max-width: 700px;
            margin: 0 auto 30px;
            opacity: 0.9;
        }

        .products-container {
            max-width: 1400px;
            margin: 0 auto;
            padding: 40px 20px;
        }

        .products-header {
            display: flex;
            justify-content: space-between;
            align-items: center;
            margin-bottom: 30px;
            flex-wrap: wrap;
            gap: 20px;
        }

        .products-title {
            font-size: 2.2rem;
            color: #26575b;
            font-weight: 700;
        }

        .products-controls {
            display: flex;
            gap: 15px;
            align-items: center;
            flex-wrap: wrap;
        }

        .filter-btn {
            background: white;
            border: 1px solid #ddd;
            padding: 10px 20px;
            border-radius: 8px;
            cursor: pointer;
            display: flex;
            align-items: center;
            gap: 8px;
            transition: all 0.3s ease;
        }

        .filter-btn:hover {
            background: #f5f5f5;
        }

        .sort-select {
            padding: 10px 15px;
            border-radius: 8px;
            border: 1px solid #ddd;
            background: white;
            cursor: pointer;
        }

        .products-content {
            display: grid;
            grid-template-columns: 280px 1fr;
            gap: 30px;
        }

        .filters-sidebar {
            background: white;
            border-radius: 12px;
            padding: 25px;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
            height: fit-content;
            position: sticky;
            top: 90px;
        }

        .filter-section {
            margin-bottom: 25px;
            padding-bottom: 20px;
            border-bottom: 1px solid #eee;
        }

        .filter-section:last-child {
            border-bottom: none;
            margin-bottom: 0;
        }

        .filter-title {
            font-size: 1.2rem;
            color: #26575b;
            margin-bottom: 15px;
            font-weight: 600;
        }

        .filter-options {
            display: flex;
            flex-direction: column;
            gap: 12px;
        }

        .filter-option {
            display: flex;
            align-items: center;
            gap: 10px;
            cursor: pointer;
        }

        .filter-option input {
            width: 18px;
            height: 18px;
            cursor: pointer;
        }

        .filter-option label {
            cursor: pointer;
            font-size: 0.95rem;
        }

        .price-range {
            display: flex;
            flex-direction: column;
            gap: 15px;
        }

        .price-inputs {
            display: flex;
            gap: 10px;
        }

        .price-inputs input {
            width: 100%;
            padding: 8px 12px;
            border: 1px solid #ddd;
            border-radius: 5px;
        }

        .price-slider {
            width: 100%;
            height: 5px;
            background: #ddd;
            border-radius: 5px;
            position: relative;
        }

        .price-slider-fill {
            position: absolute;
            height: 100%;
            background: #18685b;
            border-radius: 5px;
            left: 0%;
            right: 0%;
        }

        .products-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
            gap: 25px;
        }

        .product-card {
            background: white;
            border-radius: 12px;
            overflow: hidden;
            box-shadow: 0 5px 15px rgba(0, 0, 0, 0.05);
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            display: flex;
            flex-direction: column;
        }

        .product-card:hover {
            transform: translateY(-5px);
            box-shadow: 0 10px 25px rgba(0, 0, 0, 0.1);
        }

        .product-image {
            position: relative;
            height: 200px;
            overflow: hidden;
        }

        .product-image img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            transition: transform 0.5s ease;
        }

        .product-card:hover .product-image img {
            transform: scale(1.05);
        }

        .product-badge {
            position: absolute;
            top: 10px;
            left: 10px;
            background: linear-gradient(135deg, #ff6b6b, #ff8e53);
            color: white;
            padding: 5px 10px;
            border-radius: 4px;
            font-size: 0.8rem;
            font-weight: 600;
        }

        .product-wishlist {
            position: absolute;
            top: 10px;
            right: 10px;
            width: 35px;
            height: 35px;
            background: rgba(255, 255, 255, 0.9);
            border-radius: 50%;
            display: flex;
            align-items: center;
            justify-content: center;
            cursor: pointer;
            transition: all 0.3s ease;
        }

        .product-wishlist:hover {
            background: white;
            transform: scale(1.1);
        }

        .product-wishlist.active {
            color: #ff6b6b;
        }

        .product-info {
            padding: 20px;
            flex-grow: 1;
            display: flex;
            flex-direction: column;
        }

        .product-category {
            color: #18685b;
            font-size: 0.85rem;
            font-weight: 500;
            margin-bottom: 5px;
        }

        .product-title {
            font-size: 1.1rem;
            font-weight: 600;
            margin-bottom: 10px;
            color: #26575b;
            line-height: 1.3;
        }

        .product-rating {
            display: flex;
            align-items: center;
            gap: 5px;
            margin-bottom: 15px;
        }

        .product-rating-stars {
            color: #ffd700;
        }

        .product-rating-count {
            color: #666;
            font-size: 0.9rem;
        }

        .product-price {
            display: flex;
            align-items: center;
            gap: 10px;
            margin-bottom: 15px;
        }

        .product-price-current {
            font-size: 1.3rem;
            font-weight: 700;
            color: #26575b;
        }

        .product-price-original {
            font-size: 1rem;
            color: #999;
            text-decoration: line-through;
        }

        .product-price-discount {
            background: #ff6b6b;
            color: white;
            padding: 3px 8px;
            border-radius: 4px;
            font-size: 0.8rem;
            font-weight: 600;
        }

        .product-features {
            list-style: none;
            margin-bottom: 15px;
            flex-grow: 1;
        }

        .product-features li {
            display: flex;
            align-items: center;
            gap: 8px;
            margin-bottom: 5px;
            font-size: 0.85rem;
            color: #666;
        }

        .product-features li i {
            color: #18685b;
            font-size: 0.7rem;
        }

        .product-actions {
            display: flex;
            gap: 10px;
        }

        .btn-add-cart {
            flex-grow: 1;
            background: linear-gradient(135deg, #18685b, #26575b);
            color: white;
            border: none;
            padding: 10px;
            border-radius: 6px;
            cursor: pointer;
            font-weight: 600;
            transition: all 0.3s ease;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 8px;
        }

        .btn-add-cart:hover {
            transform: translateY(-2px);
            box-shadow: 0 5px 15px rgba(24, 104, 91, 0.3);
        }

        .btn-view {
            background: transparent;
            border: 1px solid #18685b;
            color: #18685b;
            padding: 10px 15px;
            border-radius: 6px;
            cursor: pointer;
            font-weight: 600;
            transition: all 0.3s ease;
        }

        .btn-view:hover {
            background: #18685b;
            color: white;
        }

        .pagination {
            display: flex;
            justify-content: center;
            align-items: center;
            gap: 10px;
            margin-top: 50px;
        }

        .pagination-btn {
            width: 40px;
            height: 40px;
            display: flex;
            align-items: center;
            justify-content: center;
            border-radius: 8px;
            background: white;
            border: 1px solid #ddd;
            cursor: pointer;
            transition: all 0.3s ease;
        }

        .pagination-btn.active {
            background: #18685b;
            color: white;
            border-color: #18685b;
        }

        .pagination-btn:hover:not(.active) {
            background: #f5f5f5;
        }

        .pagination-dots {
            color: #666;
        }

        /* Footer Styles */
        footer {
            background: linear-gradient(180deg, #f6e9df 0%, #fff 100%);
            padding: 48px 20px;
            border-top: 1px solid rgba(15, 122, 99, 0.07);
            margin-top: 60px;
        }

        .footer-inner {
            max-width: 1200px;
            margin: 0 auto;
            display: grid;
            gap: 28px;
            grid-template-columns: 1fr;
        }

        .footer-brand {
            display: flex;
            gap: 16px;
            align-items: flex-start;
        }

        .brand-logo {
            width: 64px;
            height: 64px;
            flex: 0 0 64px;
            border-radius: 10px;
            background: linear-gradient(135deg, #0f7a63, #0c5f4d);
            display: grid;
            place-items: center;
            color: #fff;
            font-weight: 700;
            font-size: 20px;
            box-shadow: 0 6px 18px rgba(12, 95, 77, 0.10);
        }

        .brand-info h3 {
            margin: 0 0 6px 0;
            font-size: 18px;
            line-height: 1;
        }

        .brand-info p {
            margin: 0;
            color: #5b6d70;
            font-size: 14px;
        }

        .footer-columns {
            display: grid;
            grid-template-columns: repeat(2, minmax(0, 1fr));
            gap: 18px;
        }

        .col h4 {
            margin: 0 0 12px 0;
            font-size: 15px;
            color: #20323a;
        }

        .col ul {
            list-style: none;
            padding: 0;
            margin: 0;
            display: grid;
            gap: 8px;
        }

        .col a {
            color: #5b6d70;
            text-decoration: none;
            font-size: 14px;
            transition: color .18s ease;
        }

        .col a:hover {
            color: #0c5f4d;
        }

        .footer-bottom {
            margin-top: 18px;
            display: flex;
            align-items: center;
            justify-content: space-between;
            gap: 12px;
            border-top: 1px solid rgba(15, 122, 99, 0.06);
            padding-top: 18px;
            flex-wrap: wrap;
            max-width: 1200px;
            margin: 18px auto 0;
        }

        .copyright {
            color: #5b6d70;
            font-size: 13px;
        }

        .policies {
            display: flex;
            gap: 12px;
            align-items: center;
        }

        .policies a {
            color: #5b6d70;
            font-size: 13px;
            text-decoration: none;
        }

        /* Responsive Styles */
        @media (max-width: 1024px) {
            .products-content {
                grid-template-columns: 1fr;
            }
            
            .filters-sidebar {
                position: static;
                margin-bottom: 30px;
            }
            
            .filter-options {
                flex-direction: row;
                flex-wrap: wrap;
            }
        }

        @media (max-width: 768px) {
            .products-hero h1 {
                font-size: 2.2rem;
            }
            
            .products-header {
                flex-direction: column;
                align-items: flex-start;
            }
            
            .products-controls {
                width: 100%;
                justify-content: space-between;
            }
            
            .products-grid {
                grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
            }
            
            .footer-columns {
                grid-template-columns: 1fr;
            }
        }

        @media (max-width: 576px) {
            .products-grid {
                grid-template-columns: 1fr;
            }
            
            .product-actions {
                flex-direction: column;
            }
            
            .footer-bottom {
                flex-direction: column;
                text-align: center;
            }
        }
